// B.length >= 3
// There exists some
// 	0 < i < B.length - 1
// such that
// 	B[0] < B[1] < ... B[i-1] < B[i] > B[i+1] > ... > B[B.length - 1]

// 0 <= A.length <= 10000
// 0 <= A[i] <= 10000

func longestMountain(A []int) int {
	longest, current, up := 1, 1, true

	for i, length := 1, len(A); i < length; i++ {
		if up {
			if A[i-1] == A[i] {
				current, up = 1, true
				continue
			}

			if A[i-1] < A[i] {
				current++
			} else if current > 1 {
				current++
				up = false
			}
		} else {
			if A[i-1] > A[i] {
				current++
				continue
			}

			if longest < current {
				longest = current
			}

			if length-i+1 <= longest {
				return longest
			}

			current, up = 1, true
			if A[i-1] != A[i] {
				current++
			}
		}
	}

	if !up && longest < current {
		longest = current
	}

	if longest < 3 {
		return 0
	}

	return longest
}
